INSERT INTO `m_blogs` (`id`, `title`, `view`, `thumbnail`, `type_blog`, `slug_blog`, `content`, `created_at`, `updated_at`) VALUES
(1, 'Top 6 Địa chỉ Nha khoa uy tín, được review tốt tại Quận 7 TPHCM', 14, '202206131949144848-nha-khoa-eden.jpeg', 0, 'top-6-dia-chi-nha-khoa-uy-tin-duoc-review-tot-tai-quan-7-tphcm', '<p class=\"ql-align-justify\">Dưới đây, BookingCare sẽ tổng hợp các<a href=\"https://bookingcare.vn/cam-nang/12-phong-kham-rang-uy-tin-tai-tphcm--review-nha-khoa-p2550.html\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: var(--mau-bc-16);\">&nbsp;địa chỉ nha khoa uy tín</a>&nbsp;tại Quận 7 cùng thế mạnh thăm khám để bạn đọc tham khảo và lựa chọn được địa chỉ phù hợp với nhu cầu của mình.</p><p class=\"ql-align-justify\">Tiêu chí lựa chọn Phòng khám&nbsp;<a href=\"https://bookingcare.vn/cam-nang/12-phong-kham-rang-uy-tin-tai-tphcm--review-nha-khoa-p2550.html\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: var(--mau-bc-16);\">Nha khoa Uy tín</a></p><ul><li class=\"ql-align-justify\">Có hệ thống cơ sở vật chất, trang thiết bị thăm khám đầy đủ, đảm bảo vô trùng</li><li class=\"ql-align-justify\">Chi phí minh bạch, công khai</li><li class=\"ql-align-justify\">Đội ngũ bác sĩ giỏi, uy tín, nhiều năm kinh nghiệm thăm khám</li><li class=\"ql-align-justify\">Nhận được nhiều phản hồi tích cực từ khách hàng thăm khám</li></ul><h2 class=\"ql-align-justify\">Top 6 Địa chỉ Nha khoa uy tín, được review tốt tại Quận 7 TPHCM</h2><h3 class=\"ql-align-justify\">1. Nha khoa Thẩm mỹ Queen Smile</h3><p class=\"ql-align-justify\"><strong>Thông tin về Nha khoa Kim</strong></p><p>Địa chỉ17 Đ. số 1, KP1, Quận 7, TPHCMTrang thiết bịNha khoa Queen Smile sử hữu phòng Lab chế tạo răng sứ và các vật liệu nha khoa ngay tại trung tâm, chính vì vậy sẽ hạ thấp được giá thành các dịch vụ nha khoa cho khách hàng.Đội ngũ bác sĩĐội ngũ bác sĩ được đào tạo bài bản, tốt nghiệp Bác sĩ Răng Hàm mặt, Đại học Y dược TPHCMƯu điểm nổi bậtÁp dụng công nghệ tiên tiến nhất, với liệu pháp thư giãn không đau được tiến hành bởi các chuyên gia hơn 10 năm kinh nghiệm. Bệnh nhân sẽ thấy thoải mái và dễ chịu trong suốt quá trình điều trị</p><p>Có nhiều chính sách trả góp hỗ trợ cho khách hàng</p><p class=\"ql-align-justify\">Nha khoa Queen Smile tập trung vào đối tượng khách hàng là nữ giới, hướng đến cung cấp dịch vụ chắm sóc răng miệng dành riêng cho phái nữ. Với quy trình vô trùng trang thiết bị được tuân thủ nghiêm ngặt theo tiêu chuẩn của Đức Queen Smile đảm bảo không gây lây nhiễm, an toàn tuyệt đối cho bệnh nhân.</p><p class=\"ql-align-justify\">Dịch vụ thế mạnh của Queen Smile:</p><ul><li class=\"ql-align-justify\">Cấy ghép Implant</li><li class=\"ql-align-justify\">Điều trị cười hở lợi</li><li class=\"ql-align-justify\">Nhổ răng khôn</li><li class=\"ql-align-justify\">Niềng răng thẩm mỹ</li><li class=\"ql-align-justify\">Răng miệng trẻ em</li><li class=\"ql-align-justify\">Răng sứ thẩm mỹ</li><li class=\"ql-align-justify\">Tẩy trắng răng</li></ul><p class=\"ql-align-justify\">Dưới đây là chi phí một số dịch vụ tham khảo tại Nha khoa Queen Smile, bạn đọc có thể tham khảo thêm để lựa chọn nha khoa phù hợp.</p><p><img src=\"https://bookingcare.vn/files/blog/2022/06/12/203046-chi-phi-dich-vu-nha-khoa-queen-smile.jpg\" alt=\"chi phí dịch vụ tại Nha khoa Queen Smile\">Chi phí dịch vụ tham khảo tại Nha khoa Queen Smile - Ảnh:&nbsp;nhakhoaqueensmile.com</p><p class=\"ql-align-justify\"><strong>Review của khách hàng</strong></p><p class=\"ql-align-justify\">Nha khoa Queen Smile nhận được nhiều phản hồi tích cực từ khách hàng đến thăm khám. Đặc biệt về dịch vụ khách hàng, sự thân thiện của đội ngũ bác sĩ, kỹ thuật viên hỗ trợ. Một số phản hồi bạn đọc có thể tham khảo thêm dưới đây:</p><p><img src=\"https://bookingcare.vn/files/blog/2022/06/04/205630-review-nha-khoa-queen-smile.jpg\" alt=\"review nha khoa queen smile\">Review về Nha khoa Queen Smile - Ảnh: Chụp từ Facebook</p><h3 class=\"ql-align-justify\">2. Nha khoa Ami</h3><p class=\"ql-align-justify\"><strong>Thông tin về Nha khoa Ami</strong></p><p>Địa chỉ22 Đường số 85, Khu phố 1, Quận 7, TPHCMTrang thiết bịNha khoa Ami có đầy đủ hệ thống trang thiết bị thăm khám và điều trịĐội ngũ bác sĩĐội ngũ bác sĩ tại Ami có tay nghề cao, nhiều kinh nghiệm và được nhiều khách hàng phản hồi tốtĐặc điểm nổi bậtNha Khoa Ami nằm tại vị trí dễ tìm kiếm, phòng khám có diện tích tương đối nhỏ nhưng sạch sẽ và các thiết bị được vệ sinh, sắp xếp gọn gàng</p><p><br></p><p class=\"ql-align-justify\">Tại Nha khoa Ami có cung cấp hầu hết các dịch vụ về răng như:</p><ul><li class=\"ql-align-justify\">Tẩy trắng răng, lấy vôi răng, đánh bóng</li><li class=\"ql-align-justify\">Cấy ghép Implant</li><li class=\"ql-align-justify\">Bọc răng sứ</li></ul><p><img src=\"https://bookingcare.vn/files/blog/2022/06/02/172324-trang-thiet-bi-nha-khoa-ami.jpg\" alt=\"trang thiết bị tại Nha khoa Ami\">Nha khoa Ami trang bị đầy đủ ghế ngồi riêng biệt - Ảnh: Nha khoa Ami</p><p class=\"ql-align-justify\"><strong>Review từ khách hàng</strong></p><p class=\"ql-align-justify\">Theo tìm hiểu của BookingCare trên các diễn đàn thì Nha khoa Ami nhận được nhiều phản hồi tích cực từ khách hàng thăm khám, đặc biệt về sự nhiệt tình của bác sĩ Huy.&nbsp;</p><p class=\"ql-align-justify\">Dưới đây là một số phản hồi bạn đọc có thể tham khảo thêm:</p><p><img src=\"https://bookingcare.vn/files/blog/2022/06/02/172325-review-nha-khoa-ami.jpg\" alt=\"review của khách hàng về Nha khoa Ami\">Review của khách hàng về Nha khoa Ami - Ảnh: Chụp từ Facebook</p><h3 class=\"ql-align-justify\">3. Nha khoa Việt Mỹ</h3><p class=\"ql-align-justify\">Thông tin về Nha khoa Việt Mỹ:</p><p>Địa chỉ1298 Huỳnh Tấn Phát, Phú Mỹ, Quận 7, TPHCMTrang thiết bịNha khoa Việt Mỹ có đầy đủ thiết bị thăm khám, đặc biệt Nha khoa có phòng Lab riêng sản xuất răng sứ tại chỗ.Đặc điểm nổi bậtNha Khoa Việt Mỹ có thế mạnh về răng sứ thẩm mỹ, với phòng Lab riêng đảm bảo chi phí tốt nhất cho khách hàng mà vẫn đảm bảo hiệu quả.</p><p class=\"ql-align-justify\">Nha khoa Việt Mỹ nằm tại tòa nhà riêng biệt, dễ tìm. Với cơ sở vật chất khang trang,&nbsp;<a href=\"https://bookingcare.vn/cam-nang/8-bac-si-kham-rang-gioi-tai-tphcm-p2547.html\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: var(--mau-bc-16);\">đội ngũ bác sĩ uy tín</a>, Nha khoa Việt Mỹ đã nhận được sự tin tưởng của nhiều khách hàng trong vòng 10 năm hoạt động.</p><p><img src=\"https://bookingcare.vn/files/blog/2022/06/02/175148-nha-khoa-viet-my.jpg\" alt=\"nha khoa Việt Mỹ nằm tại tòa nhà riêng biệt\">Nha khoa Việt Mỹ với cơ sở vật chất khang trang - Ảnh:&nbsp;nhakhoavietmy.com.vn</p><p class=\"ql-align-justify\">Bên cạnh dịch vụ mũi nhọn là bọc răng sứ, Nha khoa Việt Mỹ cũng cung cấp các dịch vụ chăm sóc, thẩm mỹ răng khác như:</p><ul><li class=\"ql-align-justify\">Điều trị tủy răng</li><li class=\"ql-align-justify\">Làm cầu răng</li><li class=\"ql-align-justify\">Chỉnh nha - tháo lắp</li><li class=\"ql-align-justify\"><a href=\"https://bookingcare.vn/cam-nang/top-7-dia-chi-trong-rang-implant-uy-tin-tai-tphcm-p2444.html\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: var(--mau-bc-16);\">Cấy ghép Implant</a></li></ul><p class=\"ql-align-justify\">Với dịch vụ bọc răng sứ tại Nha Khoa Việt Mỹ, phòng khám có cung cấp bọc sứ Vita - là loại sứ cao cấp được sản xuất tại Đức, với mức chi phí phù hợp nhu cầu nhiều người</p><p class=\"ql-align-justify\">Chi phí dịch vụ tham khảo tại Nha khoa Việt Mỹ:</p><p><br></p><p class=\"ql-align-justify\"><strong>Review của khách hàng</strong></p><p class=\"ql-align-justify\">Nha khoa Việt Mỹ nhận được nhiều phản hồi tích cực từ khách hàng đến thăm khám, điều trị. Bên cạnh đó cũng có một số khách hàng phản hồi chưa tốt về dịch vụ trám răng tại đây. Nếu bạn đọc có nhu cầu trám răng có thể cân nhắc các địa chỉ nha khoa khác.</p><p><img src=\"https://bookingcare.vn/files/blog/2022/06/03/135045-review-nha-khoa-viet-my.jpg\" alt=\"review về Nha khoa Việt Mỹ\">Review của khách hàng về Nha khoa Việt Mỹ - Ảnh: Chụp từ Google</p><h3 class=\"ql-align-justify\">4. Nha khoa Eden</h3><p class=\"ql-align-justify\"><strong>Thông tin về Nha khoa Eden</strong></p><ul><li>Địa chỉ171 Nguyễn Thị Thập, Tân Phú, Quận 7, TPHCMĐội ngũ bác sĩNhiều bác sĩ là chuyên gia hàng đầu trong các lĩnh vực nha khoa.&nbsp;Mỗi bác sĩ EDEN ngoài vai trò nha sĩ tổng quát còn có các hướng đi chuyên sâu vào các lĩnh vực riêng, có chứng chỉ chuyên môn, và tu nghiệp trong ngoài nướcTrang thiết bịCông nghệ CT Conebeam tái tạo 3 chiều kỹ thuật số,&nbsp;giúp chỉnh đoán chính xác tình trạng răng miệng, hỗ trợ điều trị: phẫu thuật, cấy ghép Implant, chữa tuỷ răng, nhổ răng tiểu phẫu</li><li>Máy quét dấu 3D</li><li>Hệ thống cắt răng sứ CAD/CAM</li><li>Máy Phẫu Thuật Piezotome...</li></ul><p>Đặc điểm nổi bậtĐược trang bị nhiều loại thiết bị ứng dụng công nghệ hiện đại hỗ trợ rút ngắn thời gian cho khách hàng mà vẫn đảm bảo chất lượng tốt</p><p class=\"ql-align-justify\">Nha khoa Eden với phương châm hoạt động bảo tồn răng vĩnh viễn, sức khỏe bền vững, với hệ thống thiết bị và công nghệ được cập nhật liên tục, cùng không gian sang trọng, sạch sẽ. Đây là địa chỉ nha khoa uy tín cho bạn đọc muốn chăm sóc sức khỏe răng miệng.</p><p><img src=\"https://bookingcare.vn/files/blog/2022/06/03/144848-nha-khoa-eden.jpg\" alt=\"nha khoa eden\">Nha khoa Eden có cơ sở vật chất hiện đại, sạch sẽ - Ảnh: Người dùng chia sẻ</p><p class=\"ql-align-justify\">Một số bác sĩ tại Nha khoa Eden như:</p><ul><li class=\"ql-align-justify\">Bác sĩ Nguyễn Hoàng Lộc, tốt nghiệp Bác sĩ chuyên khoa Răng Hàm mặt, chuyên sâu về chỉnh nha, nha khoa thẩm mỹ</li><li class=\"ql-align-justify\">Bác sĩ Nguyễn Phạm Minh Quân, Tốt nghiệp Bác sĩ Răng Hàm mặt, chuyên sâu về Nha khoa tổng quát, phẫu thuật miệng.</li></ul><p class=\"ql-align-justify\">Mỗi bác sĩ tại Eden sẽ chuyên sâu về 1-2 lĩnh vực nha khoa cụ thể để phục vụ khách hàng được tối ưu nhất.</p><p class=\"ql-align-justify\"><strong>Review của khách hàng</strong></p><p class=\"ql-align-justify\">Nha khoa Eden nhận được nhiều phản hồi tích cực từ khách hàng đến thăm khám, có nhiều khách hàng phản hồi rằng họ đã tin tưởng lựa chọn đến đây chăm sóc răng miệng trong nhiều năm liền.</p><p><br></p>', '2022-06-13 12:49:53', '2022-06-13 12:49:53'),
(2, 'Vì sao bị trầm cảm sau sinh?', 4, '202206132006234423-tram-cam-sau-sinh-khong-phai-mot-benh-hiem-gap.jpeg', 0, 'vi-sao-bi-tram-cam-sau-sinh-', '<h2 class=\"ql-align-justify\">Trầm cảm sau sinh là một tình trạng phổ biến</h2><p class=\"ql-align-justify\"><a href=\"https://bookingcare.vn/cam-nang/tram-cam-sau-sinh-dau-hieu-cach-dieu-tri-va-thoat-khoi-tram-cam-p564.html\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: var(--mau-bc-16);\">Trầm cảm sau sinh</a>&nbsp;có thể hiểu một cách cơ bản, là những thay đổi phức tạp về thể chất, cảm xúc và hành vi xảy ra ở một số phụ nữ sau khi sinh, từ phấn khích, vui sướng đến sợ hãi và lo lắng.&nbsp;</p><p class=\"ql-align-justify\">Trầm cảm sau sinh là một loại trầm cảm mà nhiều bậc cha mẹ gặp phải sau khi có con. Cứ 10 phụ nữ sau khi có con sẽ có khoảng 1 người mắc trầm cảm và nguy cơ tái phát trong những lần sinh sau hoặc trong quá trình nuôi dạy con, thậm chí còn gặp phải một tình trạng nghiêm trọng là rối loạn tâm thần sau sinh.</p><p class=\"ql-align-justify\">Thống kê trên thế giới cho thấy tỉ lệ trầm cảm sau sinh đối với các bà mẹ khoảng 10-20%. Tại Việt Nam, tỉ lệ trầm cảm sau sinh theo một số nghiên cứu sàng lọc có thể lên tới 33%.</p><p class=\"ql-align-justify\">Trầm cảm sau sinh thường ít được chú ý chẩn đoán và nếu không được quan tâm và điều trị kịp thời thì nó có thể gây ra những ảnh hưởng xấu đến người mẹ và đứa con mới sinh.&nbsp;</p><p class=\"ql-align-justify\">Nếu bạn cũng đang mắc trầm cảm sau sinh hay có&nbsp;<a href=\"https://bookingcare.vn/cam-nang/co-8-dau-hieu-trieu-chung-sau-co-the-ban-da-bi-tram-cam-sau-sinh-p2002.html\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: var(--mau-bc-16);\">dấu hiệu của bệnh trầm cảm sau sinh</a>, hãy liên hệ với&nbsp;<a href=\"https://bookingcare.vn/bac-si-tam-benh-tu-xa-s45\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: var(--mau-bc-16);\">bác sĩ chuyên khoa Tâm thần</a>&nbsp;để được phát hiện và điều trị sớm nhất.</p><h2 class=\"ql-align-justify\">Vì sao bị trầm cảm sau sinh?</h2><p class=\"ql-align-justify\">Hiện nay vẫn chưa thể kết luận nguyên nhân chính nào dẫn đến tình trạng trầm cảm sau sinh ở phụ nữ. Ở mỗi người sẽ do các nguyên nhân khác nhau và có những người bị, có người không.&nbsp;</p><h3 class=\"ql-align-justify\"><strong>Thay đổi nội tiết tố</strong></h3><p class=\"ql-align-justify\">Thay đổi nội tiết tố được coi là một trong các tác nhân chính gây nên chứng trầm cảm sau sinh. Khi mang thai, mức độ nội tiết tố nữ estrogen và progesterone đạt đến mức cao nhất. Trong 24 giờ đầu tiên sau khi sinh con, lượng hormone nhanh chóng giảm trở lại mức bình thường như trước khi mang thai.</p><p class=\"ql-align-justify\">Các nhà nghiên cứu cho rằng sự thay đổi đột ngột về nồng độ hormone này có thể dẫn đến&nbsp;<a href=\"https://bookingcare.vn/cam-nang/benh-tram-cam-dau-hieu-nguyen-nhan-va-cach-dieu-tri-p170.html\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: var(--mau-bc-16);\">trầm cảm</a>. Điều này tương tự như sự thay đổi hormone trước và sau kỳ kinh nguyệt của phụ nữ nhưng nghiêm trọng hơn nhiều.</p><p class=\"ql-align-justify\">Mức độ hormone tuyến giáp cũng có thể giảm sau khi sinh. Tuyến giáp là một tuyến nhỏ ở cổ giúp điều chỉnh cách cơ thể sử dụng và dự trữ năng lượng từ thức ăn. Lượng hormone tuyến giáp thấp cũng có thể gây ra các&nbsp;<a href=\"https://bookingcare.vn/cam-nang/10-trieu-chung-va-cach-tu-danh-gia-benh-tram-cam-p175.html\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: var(--mau-bc-16);\">triệu chứng trầm cảm</a>. Một xét nghiệm máu đơn giản có thể cho biết liệu tình trạng này có gây ra các triệu chứng trầm cảm hay không.</p><p><img src=\"https://bookingcare.vn/files/blog/2020/12/03/221148-thay-doi-noi-tiet-to.png\" alt=\"Thay đổi nội tiết tố sau sinh. Ảnh: Vinmec.com.\">Mô phỏng sự thay đổi nội tiết tố sau sinh - Ảnh: Vinmec</p><h3 class=\"ql-align-justify\"><strong>Tiền sử trầm cảm</strong></h3><p class=\"ql-align-justify\">Bên cạnh những thay đổi về hormone thì một trong những nguyên nhân nguy cơ gây ra trầm cảm sau sinh là người mẹ đã có sẵn tiền sử bị trầm cảm. Những phụ nữ mắc chứng trầm cảm trước, trong hoặc sau khi mang thai, hay những người đang điều trị trầm cảm có nguy cơ mắc chứng trầm cảm sau sinh cao hơn so với người bình thường.&nbsp;</p><p class=\"ql-align-justify\">Một trường hợp khác là người có người thân trong gia đình có tiền sử bị trầm cảm hoặc rối loạn lưỡng cực thì tỷ lệ mắc bệnh cũng sẽ cao.</p><h3 class=\"ql-align-justify\"><strong>Yếu tố đời sống</strong></h3><p class=\"ql-align-justify\">Gia đình không hạnh phúc, mâu thuẫn trong gia đình không được giải quyết triệt để trước và sau khi sinh cũng có thể gây trầm cảm cho bà mẹ. Áp lực chăm sóc con cái, do thiếu sự giúp đỡ của người thân, hoặc do áp lực về giới tính đứa trẻ, đứa trẻ mang thai ngoài mong muốn,&nbsp;sức khỏe thể chất của người mẹ như có hay ốm vặt, có bệnh nền trong quá trình mang thai cũng làm nhiều phụ nữ rơi vào tình trạng căng thẳng kéo dài.</p><p class=\"ql-align-justify\">Trải qua sự kiện căng thẳng như có người thân vừa qua đời, người thân trong gia đình mắc bệnh, thay đổi nơi ở, cũng là yếu tố làm tăng nguy cơ trầm cảm sau sinh.&nbsp;</p><p class=\"ql-align-justify\">Nhiều nghiên cứu đã chỉ ra những bà mẹ có con không được khỏe, con hay quấy khóc, hoặc con không bú mẹ có nguy cơ bị trầm cảm sau sinh cao những bà mẹ khác.</p><p><img src=\"https://bookingcare.vn/files/blog/2020/12/03/221635-bao-luc-gia-dinh.jpg\" alt=\"Đặc biệt, bạo hành gia đình khiến tỷ lệ mắc bệnh cao gấp 5 lần so với phụ nữ khác. Ảnh: phunuvietnam.vn.\">Bạo lực gia đình khiến tỷ lệ mắc trầm cảm sau sinh cao gấp 5 lần - Ảnh: phunuvietnam</p><h3 class=\"ql-align-justify\"><strong>Thay đổi về cảm xúc</strong></h3><p class=\"ql-align-justify\">Những cảm giác khác có thể góp phần vào chứng trầm cảm sau sinh. Nhiều bà mẹ mới sinh nói rằng họ cảm thấy:</p><ul><li class=\"ql-align-justify\">Mệt mỏi sau khi vượt cạn và sinh nở</li><li class=\"ql-align-justify\">Mệt mỏi vì thiếu ngủ hoặc mất ngủ</li><li class=\"ql-align-justify\">Giảm ham muốn tình dục và không muốn có quan hệ tình dục</li><li class=\"ql-align-justify\">Choáng ngợp với một em bé mới chào đời</li><li class=\"ql-align-justify\">Những nghi ngờ về khả năng trở thành một người mẹ tốt của họ</li><li class=\"ql-align-justify\">Căng thẳng do những thay đổi trong công việc và thói quen ở nhà</li><li class=\"ql-align-justify\">Một nhu cầu phi thực tế để trở thành một người mẹ hoàn hảo</li><li class=\"ql-align-justify\">Đau buồn vì mất con trước khi sinh con</li><li class=\"ql-align-justify\">Trở nên kém hấp dẫn</li><li class=\"ql-align-justify\">Không có thời gian rảnh</li></ul><p class=\"ql-align-justify\">Những cảm giác này đặc biệt phổ biến ở những người mới làm mẹ. Trầm cảm sau sinh là một tình trạng sức khỏe nghiêm trọng nhưng hoàn toàn có thể điều trị được, để lâu dài sẽ ảnh hưởng đến người mẹ và cả con của họ.</p><blockquote class=\"ql-align-center\"><strong>Nếu nghi ngờ bản thân hoặc người quen bị mắc trầm cảm sau sinh, bạn có thể tự kiểm tra mức độ trầm cảm bằng cách làm&nbsp;</strong><a href=\"https://www.fyrebox.com/play/test-online-djanh-gia-tra_7ZRGAeomv\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: var(--mau-bc-16);\"><strong>Bài test Online mức độ trầm cảm sau sinh EPDS</strong></a><strong>.</strong></blockquote><blockquote class=\"ql-align-center\"><a href=\"https://www.facebook.com/dialog/share?app_id=1029975067121593&amp;display=popup&amp;href=https%3A%2F%2Fbookingcare.vn%2Fcam-nang%2Fvi-sao-bi-tram-cam-sau-sinh-p2176.html&amp;redirect_uri=https%3A%2F%2Fbookingcare.vn%2Fcam-nang%2Fvi-sao-bi-tram-cam-sau-sinh-p2176.html&amp;quote=N%E1%BA%BFu%20nghi%20ng%E1%BB%9D%20b%E1%BA%A3n%20th%C3%A2n%20ho%E1%BA%B7c%20ng%C6%B0%E1%BB%9Di%20quen%20b%E1%BB%8B%20m%E1%BA%AFc%20tr%E1%BA%A7m%20c%E1%BA%A3m%20sau%20sinh%2C%20b%E1%BA%A1n%20c%C3%B3%20th%E1%BB%83%20t%E1%BB%B1%20ki%E1%BB%83m%20tra%20m%E1%BB%A9c%20%C4%91%E1%BB%99%20tr%E1%BA%A7m%20c%E1%BA%A3m%20b%E1%BA%B1ng%20c%C3%A1ch%20l%C3%A0m%C2%A0B%C3%A0i%20test%20Online%20m%E1%BB%A9c%20%C4%91%E1%BB%99%20tr%E1%BA%A7m%20c%E1%BA%A3m%20sau%20sinh%20EPDS.\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: var(--mau-bc-16);\"><strong>Chia sẻ Facebook</strong></a></blockquote><h2 class=\"ql-align-justify\">Đối tượng dễ bị trầm cảm sau sinh</h2><p class=\"ql-align-justify\"><a href=\"https://bookingcare.vn/cam-nang/tram-cam-sau-sinh-dau-hieu-cach-dieu-tri-va-thoat-khoi-tram-cam-p564.html\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: var(--mau-bc-16);\">Trầm cảm sau sinh</a>&nbsp;có thể xảy ra đối với tất cả người mẹ sau khi có con, tuy nhiên với những đối tượng dưới đây thì khả năng bị mắc sẽ cao hơn:</p><ul><li class=\"ql-align-justify\">Người có tiền sử bị bệnh trầm cảm sau sinh, nguy cơ lập lại 50%. Tiền sử trầm cảm ngoài thai kỳ, nguy cơ trầm cảm sau sinh 25%. Ngưng dùng thuốc chống trầm cảm lúc mang thai, 68% rơi vào trạng thái trầm cảm, nếu tiếp tục dùng thuốc thì 25% bị trầm cảm.</li><li class=\"ql-align-justify\">Đang trong độ tuổi dưới 18.</li><li class=\"ql-align-justify\">Trải qua những sự kiện gây căng thẳng trong thời gian trước: bệnh tật, hiếm muộn, thất nghiệp, biến cố gia đình...</li><li class=\"ql-align-justify\">Thiếu sự giúp đỡ, đồng cảm chia sẻ của người thân, đặc biệt là người chồng.</li><li class=\"ql-align-justify\">Mâu thuẫn vợ chồng, mâu thuẫn với mẹ chồng.</li><li class=\"ql-align-justify\">Thai kỳ không mong muốn.</li><li class=\"ql-align-justify\">Biến chứng thai kỳ: thai lưu, sẩy thai.</li><li class=\"ql-align-justify\">Trầm cảm dễ xuất hiện ở người con so, tuy nhiên vẫn có thể xảy ra ở con rạ.</li><li class=\"ql-align-justify\">Lớn lên trong môi trường gia đình thiếu sự hạnh phúc, hoặc có trải nghiệm tuổi thơ tiêu cực như lạm dụng thể chất hoặc tình dục hoặc tinh thần,...</li></ul><p class=\"ql-align-justify\"><strong>Xem thêm</strong></p><ul><li class=\"ql-align-justify\"><a href=\"https://bookingcare.vn/cam-nang/7-dau-hieu-bieu-hien-tram-cam-sau-sinh-p2171.html\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: var(--mau-bc-16);\"><strong>7 dấu hiệu trầm cảm sau sinh&nbsp;</strong></a></li><li class=\"ql-align-justify\"><a href=\"https://bookingcare.vn/cam-nang/5-yeu-to-giup-vo-thoat-khoi-tram-cam-sau-sinh-p2098.html\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: var(--mau-bc-16);\"><strong>5 yếu tố giúp vợ thoát khỏi trầm cảm sau sinh</strong></a></li></ul><h2 class=\"ql-align-justify\">Trầm cảm sau sinh có nguy hiểm không?</h2><p class=\"ql-align-justify\">Theo&nbsp;<a href=\"https://bookingcare.vn/thac-si-chuyen-gia-tam-ly-tran-thi-tuyet-hong-tu-van-tu-xa-d1447.html\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: var(--mau-bc-16);\">Chuyên gia Trần Thị Tuyết Hồng</a>, đầu tiên, trầm cảm sau sinh có thể gây ra những tác động tiêu cực đến thể chất lẫn tinh thần của người mẹ. Ở dạng trầm cảm nhẹ, người mẹ thường thấy mệt mỏi, mất ngủ, lo lắng thái quá đối với sức khỏe của con và của bản thân...</p><p class=\"ql-align-justify\">Nếu bị trầm cảm nặng, người mẹ trở nên buồn rầu, rối loạn giấc ngủ và ăn uống, hay cáu gắt vô cớ, có những suy nghĩ và hành động với đứa con mới đẻ và những đứa trẻ khác&nbsp;mặc dù nếu được hỏi thì người mẹ nghĩ rằng vấn rất yêu thương con mình.</p><p class=\"ql-align-justify\">Bên cạnh những ảnh hưởng đến sức khỏe người mẹ,&nbsp;<a href=\"https://bookingcare.vn/cam-nang/tram-cam-sau-sinh-dau-hieu-cach-dieu-tri-va-thoat-khoi-tram-cam-p564.html\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: var(--mau-bc-16);\">trầm cảm sau sinh</a>&nbsp;còn ảnh hưởng rất nhiều đến em bé. Trầm cảm sau sinh ảnh hưởng đến sự tương tác giữa mẹ và con. Các bà mẹ bị trầm cảm sau sinh thường biểu hiện thái độ không quan tâm, thờ ơ và vô cảm với nhu cầu đòi hỏi cần mẹ của đứa trẻ, đôi khi là khó chịu, thù địch với đứa con của mình.</p><p><img src=\"https://bookingcare.vn/files/blog/2020/12/04/200147-190320-senapathy-depression-teaseqrxueo.jpeg\" alt=\"Người mẹ sẽ cảm thấy \">Người mẹ sẽ cảm thấy \"khó khăn\" với chính đứa con của mình - Ảnh: feminisminindia</p><p class=\"ql-align-justify\">Họ ít tham gia vào chăm sóc trẻ, ít biểu lộ cảm xúc và chơi đùa với trẻ. Những người mẹ bị trầm cảm sẽ cảm thấy không liên kết với con, như thể đây không phải con của họ. Nghiêm trọng hơn khi người mẹ cảm thấy mất ý nghĩa, niềm vui trong cuộc sống và có suy nghĩ tiêu cực nếu để đứa trẻ sống không có mẹ sẽ là có tội, dẫn đến ý tưởng mang con cùng tự tử.&nbsp;</p><p class=\"ql-align-justify\">Sự thiếu tương tác, quan tâm của người mẹ làm ảnh hưởng đến sự phát triển về nhận thức và kỹ năng giao tiếp của trẻ. Một số nghiên cứu đã cho thấy trẻ sơ sinh của các bà mẹ bị trầm cảm ít biểu cảm ngôn ngữ hơn và chức năng nhận thức ngôn ngữ cũng kém hơn, trẻ cũng kém linh hoạt hơn so với những trẻ khác.&nbsp;</p><p class=\"ql-align-justify\">Bên cạnh đó, trầm cảnh sau sinh cũng ảnh hưởng đến các mối quan hệ xã hội xung quanh như quan hệ vợ chồng, quan hệ với người thân trong gia đình hay bạn bè...</p><p class=\"ql-align-justify\">Trầm cảm sau sinh không phải là một khiếm khuyết về trí não hay cảm xúc mà nó là do những căng thẳng trước, trong quá trình mang thai, sau khi sinh nở mà một người phụ nữ gánh vác không có đủ điều kiện, sự chăm sóc cả thể chất và tinh thần từ người thân kết hợp với những nguyên nhân tiềm ẩn vốn có là yếu tố góp phần thúc đẩy khởi phát triệu chứng của trầm cảm sau khi sinh. Bệnh tiềm tàng nhiều nguy hiểm nhưng hoàn toàn có thể điều trị được.</p><p><br></p>', '2022-06-13 13:06:16', '2022-06-13 13:06:16'),
(3, 'Trẻ bị mẩn đỏ là bệnh gì? Cách điều trị khi trẻ bị mẩn đỏ', 5, '202206132008105938-tre-bi-noi-man-do-2.png', 0, 'tre-bi-man-do-la-benh-gi-cach-dieu-tri-khi-tre-bi-man-do', '<p class=\"ql-align-justify\">Trẻ em bị nổi mẩn đỏ đi kèm cảm giác châm chích, ngứa ngáy là dấu hiệu cảnh báo có thể trẻ bị mắc một số bệnh lý về da.</p><p class=\"ql-align-justify\">Cha mẹ cần theo dõi các triệu chứng và cho con&nbsp;<a href=\"https://bookingcare.vn/bac-si-da-lieu-tu-xa-s44?s=da+l\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: var(--mau-bc-16);\">thăm khám bác sĩ da liễu</a>&nbsp;để được chữa trị kịp thời, tránh dẫn tới những biến chứng khó lường.</p><h2 class=\"ql-align-justify\">Nổi mẩn đỏ là gì?</h2><p class=\"ql-align-justify\">Nổi mẩn là tên gọi chung của nhiều dạng thương tổn da khác nhau, có thể là các sẩn phù như mề đay hoặc là những mụn li ti nhỏ.&nbsp;</p><p class=\"ql-align-justify\">Da của trẻ sơ sinh có thể có nhiều thay đổi trong bốn tuần đầu của cuộc đời. Hầu hết tình trạng nổi mẩn đỏ ở trẻ sơ sinh là lành tính và tự giới hạn.</p><p class=\"ql-align-justify\">Khi bé bị nổi mẩn đỏ, mẹ nên để ý những thay đổi trên da của bé hay gặp trong giai đoạn này để biết khi nào nên khám bác sĩ.</p><h2 class=\"ql-align-justify\">Nguyên nhân khiến trẻ bị mẩn đỏ</h2><h3 class=\"ql-align-justify\">1. Ban đỏ nhiễm độc ở trẻ sơ sinh</h3><p class=\"ql-align-justify\">Ban đỏ nhiễm độc có thể là nguyên nhân khiến trẻ sơ sinh bị nổi mẩn đỏ ở mặt và toàn thân. Là bệnh lành tính, tự giới hạn xuất hiện ở trẻ sơ sinh khoẻ mạnh.</p><p class=\"ql-align-justify\">Tỷ lệ mắc bệnh thay đổi từ 40 đến 70 %, phổ biến nhất ở trẻ sinh ra và cân nặng dưới 2500 g.</p><p class=\"ql-align-justify\">Các biểu hiện có thể thấy sau khi sinh nhưng thường xuất hiện vào ngày thứ hai hoặc thứ ba sau sinh, có trường hợp khởi phát sau 14 ngày.&nbsp;</p><p class=\"ql-align-justify\"><strong>Biểu hiện của bệnh:</strong></p><ul><li class=\"ql-align-justify\">Tổn thương là những dát đỏ, sẩn đỏ có đường kính 2-3 mm, mụn nước, mụn mủ. Khi mất đi không để lại di chứng gì. Số lượng và vị trí tổn thương thay đổi tuỳ từng trường hợp.</li><li class=\"ql-align-justify\">Triệu chứng toàn thân: không sốt, không có các triệu chứng về thần kinh.</li><li class=\"ql-align-justify\">Vị trí thường gặp nhất là bé bị nổi mẩn đỏ ở mặt, thân người, gốc chi; hiếm khi có tổn thương ở gan bàn tay, gan bàn chân, niêm mạc.</li><li class=\"ql-align-justify\">Tổn thương tự thoái lui sau 5-14 ngày và không để lại dấu vết gì.</li></ul><p class=\"ql-align-justify\"><strong>Nguyên nhân:</strong>&nbsp;chưa rõ nguyên nhân</p><p class=\"ql-align-justify\"><strong>Điều trị:</strong>&nbsp;Trẻ bị nổi mẩn đỏ trong trường hợp này là bệnh lành tính, tự giới hạn, không cần điều trị.</p><h3 class=\"ql-align-justify\">2. Mụn trứng cá ở trẻ sơ sinh</h3><p class=\"ql-align-justify\"><a href=\"https://bookingcare.vn/cam-nang/tre-so-sinh-bi-mun-co-nguy-hiem-khong-khi-nao-can-di-kham-p2003.html\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: var(--mau-bc-16);\">Mụn</a>&nbsp;trứng cá cũng là nguyên nhân khiến trẻ sơ sinh nổi mẩn ngứa thường gặp.&nbsp;Mụn trứng cá chiếm tỷ lệ khoảng 20% trẻ sơ sinh và thường khởi phát ở tuần thứ 3.</p><p class=\"ql-align-justify\"><strong>Biểu hiện của bệnh:</strong></p><ul><li class=\"ql-align-justify\">Thương tổn là là các sẩn đỏ viêm và mụn mủ như mụn trứng cá, còn thương tổn dạng nang hiếm khi xảy ra.</li><li class=\"ql-align-justify\">Vị trí thường gặp nhất là ở mặt, trán…</li></ul><p class=\"ql-align-justify\"><strong>Nguyên nhân:</strong>&nbsp;do sự kích thích androgen của tuyến nhờn bởi các hormone mẹ.</p><p class=\"ql-align-justify\"><strong>Điều trị:</strong>&nbsp;thường không cần thiết vì tổn thương tự hết trong vòng 1-3 tháng. Tuy nhiên, các chế phẩm mụn trứng cá có thể được sử dụng với nguy cơ tối thiểu trong các trường hợp nặng hoặc kéo dài.</p><h3 class=\"ql-align-justify\">2. Viêm da tiết bã ở trẻ sơ sinh</h3><p class=\"ql-align-justify\"><strong>Biểu hiện của bệnh:</strong></p><ul><li class=\"ql-align-justify\">Trên nền hồng ban có hiện tượng bong tróc vảy nhỏ màu vàng, nhờn, giới hạn tương đối rõ gây ra biểu hiện ngứa gây ra cảm giác khó chịu cho bé.</li><li class=\"ql-align-justify\">Vị trí: tập trung chủ yếu ở các vùng da nhiều tuyến bã như trên da đầu, ở mặt và vùng thân trên cơ thể, vì vậy được gọi là bệnh&nbsp;<a href=\"https://bookingcare.vn/cam-nang/viem-da-tiet-ba--trieu-chung-nguyen-nhan-va-cach-dieu-tri-p2088.html\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: var(--mau-bc-16);\">viêm da tiết bã.</a></li></ul><p class=\"ql-align-justify\"><strong>Nguyên nhân:</strong>&nbsp;do nấm Malassezia spp gây ra phản ứng viêm.</p><p class=\"ql-align-justify\"><strong>Điều trị:</strong>&nbsp;các bác sĩ sẽ chỉ định các thuốc bôi tùy theo diện tích, mức độ và có bội nhiễm kèm theo hay không.</p><h3 class=\"ql-align-justify\">3. Hăm</h3><p class=\"ql-align-justify\">Bé nổi mẩn đỏ thường là do hăm.&nbsp;Thường gặp ở trẻ em, nhất là trẻ em mập mạp, hoặc ra mồ hôi nhiều,do ứ đọng nước tiểu, ứ đọng phân do tiêu chảy.&nbsp;</p><p class=\"ql-align-justify\"><strong>Biểu hiện của bệnh</strong>:</p><ul><li class=\"ql-align-justify\">Các nếp kẽ trên chuyển thành đám đỏ, trợt, rỉ dịch, do cọ sát, đám trợt loét rỉ dịch và gây đau.</li><li class=\"ql-align-justify\">Nếu bội nhiễm vi trùng và nấm có thể làm sưng tấy tổn thương,làm chảy mủ và rỉ dịch nhiều hơn.</li><li class=\"ql-align-justify\">Vị trí: các nếp cổ, nếp bẹn, kẽ mông, kẽ sau tai, có khi ở rốn, các ngấn da và vùng xung quanh hậu môn (khi trẻ bị tiêu chảy).</li></ul><p class=\"ql-align-justify\"><strong>Nguyên nhân:</strong>&nbsp;Xảy ra thường xuyên khi mặc tả, ngay cả khi tả khô hay ướt. Hăm lở da do ẩm ướt: do bỉm đầy, đọng nước tiểu hoặc phân, tiếp xúc lâu với làn da của bé sẽ gây hăm ở những nếp cọ gấp.&nbsp;</p><p class=\"ql-align-justify\"><strong>Điều trị:</strong>&nbsp;đa phần tự hết nếu giữ gìn vệ sinh tốt, tránh ẩm ướt, nếu nặng bác sĩ sẽ chỉ định thuốc bôi.</p><h3 class=\"ql-align-justify\">4. Rôm sảy</h3><p class=\"ql-align-justify\"><a href=\"https://bookingcare.vn/cam-nang/luu-y-khi-tre-bi-rom-say-cach-tri-rom-say-cho-tre-tai-nha-p2032.html\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: var(--mau-bc-16);\">Rôm sảy</a>&nbsp;là biểu hiện ở trẻ sơ sinh nổi mẩn đỏ với nhiều sẩn nhỏ, trên có mụn nước nhỏ, đôi khi có mụn mủ trắng xen lẫn như đầu đinh ghim, lấm tấm màu hồng, mọc thành từng đám, có khi dày đặc. Bé có thể bị ngứa ngáy biểu hiện khó chịu, quấy khóc.</p><p><img src=\"https://bookingcare.vn/files/blog/2020/12/22/103643-rom-say.png\" alt=\"Rôm sảy là một trong những nguyên nhân khiến trẻ bị mẩn đỏ - Ảnh: Vinmec\">Rôm sảy là một trong những nguyên nhân khiến trẻ bị mẩn đỏ - Ảnh: Vinmec</p><p class=\"ql-align-justify\">Vị trí ở vùng đầu, cổ và vai, lưng và các nếp gấp của cơ thể.</p><p class=\"ql-align-justify\"><strong>Nguyên nhân:</strong>&nbsp;do bít tắc tuyến mồ hôi gây ra rôm sảy.</p><p class=\"ql-align-justify\"><strong>Điều trị:</strong>&nbsp;đa phần tự hết nếu giữ gìn vệ sinh tốt, tránh môi trường nắng nóng, nếu nặng khi có dấu hiệu bội nhiễm bác sĩ sẽ chỉ định thuốc bôi.</p><h2 class=\"ql-align-justify\">Cách điều trị khi trẻ bị mẩn đỏ</h2><p class=\"ql-align-justify\">Rất nhiều bà mẹ do không hiểu về tình trạng của trẻ nên đã lo lắng thái quá và chăm sóc con chưa đúng cách. Thậm chí, nhiều mẹ còn không dám đụng vào con vì sợ những nốt mụn sẽ lan rộng và vỡ ra.</p><p class=\"ql-align-justify\">Theo các chuyên gia khuyến cáo, đối với những bệnh về da ở trẻ, bố mẹ không nên kiêng khem quá mức mà nên làm theo hướng dẫn sau:</p><h3 class=\"ql-align-justify\">Vệ sinh da cho con</h3><p class=\"ql-align-justify\">Không chỉ vào những ngày con khỏe mạnh, ngay cả những ngày con bị ốm hay bị viêm da, cha mẹ cũng cần chú ý vệ sinh cho con thật kỹ.</p><h3 class=\"ql-align-justify\">Thăm khám bác sĩ chuyên khoa</h3><p class=\"ql-align-justify\">Những nguyên nhân được liệt kê trên đây chỉ mang tính chất tham khảo. Cha mẹ không nên tự ý mua thuốc về điều trị cho con dựa theo phán đoán và kinh nghiệm cá nhân.&nbsp;</p><p class=\"ql-align-justify\">Để tránh mắc sai lầm khi chăm sóc cho bé, hoặc khi bé có biểu hiện quấy khóc, khó chịu vì mẩn ngứa, cha mẹ nên cho con thăm khám với&nbsp;<a href=\"https://bookingcare.vn/bac-si-da-lieu-tu-xa-s44?s=da\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: var(--mau-bc-16);\"><strong>bác sĩ Da liễu từ xa</strong></a>&nbsp;để được tư vấn biện pháp khắc phục, xử lý kịp thời.</p><h2 class=\"ql-align-justify\">Một số lưu ý trong quá trình chăm sóc trẻ khi trẻ bị nổi mẩn đỏ</h2><h3 class=\"ql-align-justify\">Những điều không nên làm khi trẻ bị nổi mẩn đỏ</h3><ul><li class=\"ql-align-justify\">Không tắm hoặc lau rửa cho bé quá kỹ. Da của bé rất mỏng nếu lau rửa quá kỹ, da của bé rất dễ bị kích ứng.&nbsp;</li><li class=\"ql-align-justify\">Mẹ tuyệt đối không được nặn hay làm vỡ mụn ở vùng da bị mẩn đỏ. Vì khi những nốt mụn vỡ ra, bé có thể bị nhiễm trùng.</li><li class=\"ql-align-justify\">Không thoa lên da bé các loại kem không rõ nguồn gốc.&nbsp;</li><li class=\"ql-align-justify\">Không dùng các loại sữa tắm có chất tạo bọt, tẩy rửa vì những chất này tiếp xúc với da bé sẽ khiến da của bé đỏ và ngứa nặng hơn.&nbsp;</li></ul><h3 class=\"ql-align-justify\">Những điều nên làm khi trẻ bị nổi mẩn đỏ</h3><p class=\"ql-align-justify\">Khi trẻ có hiện tượng nổi mẩn đỏ, cha mẹ nên hạn chế cho bé tiếp xúc với các tác nhân kích ứng và nên chú ý thực hiện những điều sau để làn da của bé nhanh chóng được cải thiện:&nbsp;</p><ul><li class=\"ql-align-justify\">Giữ vệ sinh cơ thể cho bé bằng cách tắm hoặc lau người cho bé.&nbsp;</li><li class=\"ql-align-justify\">Mẹ cần vệ sinh cơ thể và miệng của trẻ sạch sẽ sau khi cho trẻ ăn và bú.</li><li class=\"ql-align-justify\">Không nên để trẻ ở một không gian quá nóng bức, ngột ngạt hoặc quá ẩm ướt.&nbsp;</li><li class=\"ql-align-justify\">Mẹ cần quan sát bé để bé không cào, gãi lên vùng da bị mẩn đỏ, tránh làm da trầy xước, tạo cơ hội cho các loại vi khuẩn xâm nhập vào da.&nbsp;</li><li class=\"ql-align-justify\">Mẹ nên chọn những loại quần áo được làm từ chất liệu thoáng mát, mềm mại và có thể thấm hút tốt giúp trẻ thoải mái khi mặc và tránh việc quần áo cọ vào những nốt mụn gây đau và khó chịu.</li><li class=\"ql-align-justify\">Nên dùng những loại sữa tắm nhẹ nhàng, không có tính tẩy rửa quá mạnh.&nbsp;</li><li class=\"ql-align-justify\">Nên bổ sung cho trẻ nhiều nước, sữa và các loại thức uống có tính mát giúp bé tăng cường sức đề kháng.</li></ul><p class=\"ql-align-justify\"><img src=\"https://bookingcare.vn/files/blog/2020/12/22/103635-rom-say-1.png\" alt=\"Giữ vệ sinh cơ thể cho bé để ngăn ngừa các bệnh về da\">Giữ vệ sinh cơ thể cho bé để ngăn ngừa các bệnh về da - Ảnh: Bách hóa xanh</p><p><br></p>', '2022-06-13 13:08:50', '2022-06-13 13:08:50'),
(4, 'Kit test xét nghiệm nhanh Covid-19 qua nước bọt', 4, '202206132011172355-kit-test-nhanh-covid-qua-nuoc-bot-lyher.jpeg', 1, 'kit-test-xet-nghiem-nhanh-covid-19-qua-nuoc-bot', '<p class=\"ql-align-justify\">Kit test xét nghiệm nhanh Novel Coronavirus (COVID-19) Antigen Test Kit (Colloidal Gold) là một trong số ít loại kit test nhanh bằng dịch nước bọt được&nbsp;<a href=\"https://bookingcare.vn/cam-nang/18-loai-kit-test-nhanh-covid-duoc-bo-y-te-cap-phep--cach-chon-loai-kit-chat-luong-tot-p2644.html\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: var(--mau-bc-16);\">Bộ Y tế phê duyệt</a>&nbsp;lưu hành tại Việt Nam cho đến thời điểm hiện tại.</p><h2 class=\"ql-align-justify\">Kit test xét nghiệm nhanh Covid-19 qua nước bọt, Hãng Lyher</h2><p class=\"ql-align-justify\">Novel Coronavirus (COVID-19) Antigen Test Kit (Colloidal Gold) là bộ xét nghiệm nhanh nhằm&nbsp;phát hiện trực tiếp và định tính kháng nguyên&nbsp;(N-protein) của virus SARS-CoV-2 từ mẫu dịch&nbsp;nước bọt.</p><ul><li class=\"ql-align-justify\">Tên sản phẩm:&nbsp;Novel Coronavirus (COVID-19) Antigen Test Kit (Colloidal Gold)</li><li class=\"ql-align-justify\">Nhà sản xuất: LYHER</li><li class=\"ql-align-justify\">Sản xuất tại: Trung Quốc</li><li class=\"ql-align-justify\">Sản phẩm có chứng chỉ CE Châu Âu, ISO 13485</li><li class=\"ql-align-justify\">Được cấp phép lưu hành tại các nước châu Âu: Hà Lan, Italy,...</li><li class=\"ql-align-justify\">Nhà máy sản xuất được FDA Hoa Kỳ chứng nhận đạt tiêu chuẩn.</li><li class=\"ql-align-justify\">Mẫu xét nghiệm bằng nước bọt: ít khó chịu, dễ thực hiện.</li><li class=\"ql-align-justify\"><a href=\"https://bookingcare.vn/cam-nang/test-covid-bang-nuoc-bot-co-chinh-xac-khong-kit-test-nuoc-bot-hay-dich-mui-tot-hon-p2676.html\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: var(--mau-bc-16);\">Độ nhạy lâm sàng</a>: 95.58%&nbsp;</li><li class=\"ql-align-justify\">Độ đặc hiệu lâm sàng: 99.47%</li></ul><h2 class=\"ql-align-justify\">Giá kit test nhanh qua nước bọt</h2><ul><li class=\"ql-align-justify\">Giá theo bộ kit: 75.000đ/kit</li><li class=\"ql-align-justify\">Giá theo hộp: 1.625.000đ/hộp (25 kit test, 65.000đ/kit)</li></ul><p class=\"ql-align-justify\">Độ nhạy và độ đặc hiệu của Kit test nhanh Covid 19 qua nước bọt Novel Coronavirus (COVID-19) Antigen Test Kit (Colloidal Gold)&nbsp;tương đương với nhiều loại&nbsp;<a href=\"https://bookingcare.vn/cam-nang/18-loai-kit-test-nhanh-covid-duoc-bo-y-te-cap-phep--cach-chon-loai-kit-chat-luong-tot-p2644.html\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: var(--mau-bc-16);\">kit test nhanh bằng dịch tỵ hầu</a>&nbsp;khác đã được Bộ Y tế phê duyệt trước đó.</p><h2 class=\"ql-align-justify\">Sản phẩm đã được cấp phép lưu hành&nbsp;</h2><p class=\"ql-align-justify\">Kit test nhanh Novel Coronavirus (COVID-19) Antigen Test Kit (Colloidal Gold)&nbsp;- LYHER đã được Bộ Y tế cấp phép lưu hành tại Việt Nam, số 2100018/LHCB/BYT-TB-CT.</p><p><img src=\"https://bookingcare.vn/files/blog/2022/01/25/092747-giay-phep-luu-hanh-byt.jpg\" alt=\"Giấy phép BYT\">Giấy chứng nhận lưu hành Kit Test nhanh nước bọt do Bộ Y tế cấp</p><p class=\"ql-align-justify\">Đồng thời được kiểm định chất lượng tại Viện kiểm định Quốc Gia Vắc xin và Sinh phẩm y tế.</p><p><img src=\"https://bookingcare.vn/files/blog/2022/01/25/093014-chung-nhan-kiem-dinh.jpg\" alt=\"Chứng nhận kiểm nghiệm\">Giấy chứng nhận kiểm nghiệm do&nbsp;Viện kiểm định Quốc Gia Vắc xin và Sinh phẩm y tế cấp.</p><h2 class=\"ql-align-justify\">Hướng dẫn sử dụng Kit test nhanh Covid -19 qua nước bọt</h2><h3 class=\"ql-align-justify\">Thành phần bộ Kit test nhanh nước bọt Novel Coronavirus (COVID-19) Antigen Test Kit (Colloidal Gold)</h3><p class=\"ql-align-justify\">Trong 1 hộp kit test nhanh Lyher được đóng gói sẽ bao gồm 25 kit test nhanh:</p><ul><li class=\"ql-align-justify\">Khay xét nghiệm</li><li class=\"ql-align-justify\">Ống chiết mẫu chứa đệm</li><li class=\"ql-align-justify\">Đế đựng ống mẫu</li><li class=\"ql-align-justify\">Ống nhỏ giọt dùng 1 lần</li><li class=\"ql-align-justify\">Cốc giấy thu mẫu</li><li class=\"ql-align-justify\">Hướng dẫn sử dụng nhanh</li><li class=\"ql-align-justify\">Hướng dẫn sử dụng chi tiết</li></ul><p><img src=\"https://bookingcare.vn/files/blog/2022/01/21/163000-bo-kit-test-nuoc-bot-lyher.jpg\" alt=\"bộ kit test nước bọt lyher\">Bộ Kit test nhanh Covid Lyher qua nước bọt - Ảnh: BookingCare</p><h3 class=\"ql-align-justify\">10 bước hướng dẫn sử dụng Kit test nhanh&nbsp;nước bọt Novel Coronavirus (COVID-19) Antigen Test Kit (Colloidal Gold)</h3><p class=\"ql-align-justify\">Trước tiên để&nbsp;<a href=\"https://bookingcare.vn/cam-nang/luu-y-quan-trong-khi-su-dung-kit-test-nhanh-covid-qua-nuoc-bot-p2679.html\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: var(--mau-bc-16);\">thực hiện test nhanh</a>&nbsp;trên Kit test nhanh Novel Coronavirus (COVID-19) Antigen Test Kit (Colloidal Gold),bạn đọc cần lấy mẫu nước bọt bằng cách:</p><ul><li class=\"ql-align-justify\">Súc miệng bằng nước để loại bỏ thức ăn thừa</li><li class=\"ql-align-justify\">Khạc nước bọt từ cổ họng vào cốc giấy dùng 1 lần để thu mẫu bệnh phẩm</li></ul><p class=\"ql-align-justify\"><strong>Lưu ý:</strong>&nbsp;KHÔNG ăn, uống, đánh răng, hút thuốc lá, nhai kẹo cao su 30 phút trước khi lấy mẫu</p><p class=\"ql-align-justify\">Sau đó, bạn đọc sẽ cần thực hiện test nhanh theo 10 bước chi tiết trong hình sau:</p><p><img src=\"https://bookingcare.vn/files/blog/2022/01/21/164300-quy-trinh-xet-nghiem-kit-test-lyher.jpg\" alt=\"10 bước xé nghiệm test nhanh covid bằng kit test bước bọt lyher\">10 bước test nhanh Covid19 bằng kit test nước bọt Novel Coronavirus (COVID-19) Antigen Test Kit (Colloidal Gold)</p><p>- Ảnh: BookingCare</p><p class=\"ql-align-justify\">Ngoài ra, để dễ dàng cho việc thực hiện test nhanh bằng nước bọt, bạn đọc có thể xem video hướng dẫn dưới đây.</p><p><br></p>', '2022-06-13 13:11:48', '2022-06-13 13:11:48'),
(5, 'Máy Đo Nồng Độ Oxy Trong Máu - Microlife Oxy 200', 1, '202206132012101653-may-do-spo2-microlife.jpeg', 1, 'ma-y-do-no-ng-do-oxy-trong-ma-u-microlife-oxy-200', '<p><img src=\"https://cdn.bookingcare.vn/fr/w800/2022/02/22/101653-may-do-spo2-microlife.jpg\" alt=\"Máy Đo Nồng Độ Oxy Trong Máu - Microlife Oxy 200\">Máy đo nồng độ Oxy máu - Microlife Oxy 200 - Ảnh: Bách Hóa Y tế</p><p class=\"ql-align-justify\">Chỉ số nồng độ oxi bão hòa SpO2 trong máu rất quan trọng của hệ thống tuần hoàn hô hấp. Các bệnh về hô hấp có thể dẫn đến giảm nồng độ oxi bão hòa trong máu bệnh nhân.</p><p class=\"ql-align-justify\">Một người khỏe mạnh bình thường khi thở ở không khí sẽ có độ bão hòa oxy động mạch là 95%-100%. Do đó, chỉ số SPO2 là chỉ số sinh tồn của cơ thể.</p><h2 class=\"ql-align-justify\">Chức năng máy đo nồng độ oxy máu Oxy 200</h2><ul><li class=\"ql-align-justify\">Dùng để đo nồng độ oxy trong máu và nhịp tim của cả người lớn và bệnh nhi</li><li class=\"ql-align-justify\">Đo ở ngón tay, không đau khi đo.</li><li class=\"ql-align-justify\">Có thể đo cho nhiều người</li><li class=\"ql-align-justify\">Màn hình LED.</li><li class=\"ql-align-justify\">Thiết bị sẽ cảnh báo nếu phát hiện nhịp tim dưới 50 nhịp/phút, cao hơn 130 nhịp/phút hoặc chỉ số SpO2 thấp hơn 94%.</li><li class=\"ql-align-justify\">Thích hợp dùng cho gia đình hoặc phòng khám, bệnh viện hoặc khi duy chuyển.</li><li class=\"ql-align-justify\">Tự động tắt sau đo.</li></ul><p><img src=\"https://bookingcare.vn/files/blog/2022/02/20/164425-spo2microlifeoxy200.jpg\" alt=\"Máy đo SpO2\">Máy đo nồng độ Oxy máu - Ảnh: Bách hóa Y tế</p><h2 class=\"ql-align-justify\">Công dụng máy đo Microlife Oxy 200</h2><p class=\"ql-align-justify\">Microlife Oxy 200 - Máy Đo Nồng Độ Oxy Trong Máu là một thiết bị y tế cải tiến với các tính năng không xâm lấn và liên tục để phát hiện nồng độ oxy trong máu (SPO2),nhịp tim (PR) của cả người lớn và bệnh nhi.</p><p class=\"ql-align-justify\">Mang tính di động nên sản phẩm có thể đo các giá trị nồng độ oxy máu (SPO2) và nhịp tim (PR) một cách nhanh chóng, chính xác.</p><p class=\"ql-align-justify\"><br></p><h2 class=\"ql-align-justify\">Cách dùng&nbsp;Microlife Oxy 200</h2><ul><li class=\"ql-align-justify\">Lắp 2 pin AAA vào hộp pin trước khi đậy nắp pin.</li><li class=\"ql-align-justify\">Cắm 1 ngón tay vào lỗ cao su của máy (tốt nhất là cắm kỹ ngón tay) trước khi thả kẹp với móng tay hướng lên trên.</li><li class=\"ql-align-justify\">Nhấn nút (nút màu&nbsp;đen/trắng) trên máy.</li><li class=\"ql-align-justify\">Không run ngón tay khi đang dùng thiết bị để đo, cơ thể nên giữ nguyên không di chuyển.</li><li class=\"ql-align-justify\">Đọc dữ liệu liên quan từ màn hình hiển thị.</li><li class=\"ql-align-justify\">Thiết bị có chức năng tự động tắt sau khi đo</li></ul><p class=\"ql-align-justify\">Quy<strong> cách đóng gói</strong></p><p><br></p><ul><li class=\"ql-align-justify\">01 Máy đo nồng độ oxy, túi đựng, hướng dẫn sử dụng, phiếu bảo hành chính hãng.</li><li class=\"ql-align-justify\">Nhà sản xuất: Microlife (Thụy Sĩ)</li><li class=\"ql-align-justify\">Nước sản xuất: China (Trung Quốc)</li></ul><p><img src=\"https://bookingcare.vn/files/blog/2022/02/20/164537-spo2-microlife-oxy-200.jpg\"></p><p>Đóng gói máy SpO2 - Ảnh: Nhà thuốc Long Châu</p><h2 class=\"ql-align-justify\">Chính sách bán hàng</h2><ul><li class=\"ql-align-justify\">Giá bán sản phẩm: 780,000 VNĐ</li><li class=\"ql-align-justify\">Hàng Chính hãng và Chất lượng Cao cấp.</li><li class=\"ql-align-justify\">Cam kết hàng mới 100%</li><li class=\"ql-align-justify\">Giao hàng toàn quốc / tư vấn nhiệt tình.</li><li class=\"ql-align-justify\">Bảo hành: 2 năm</li></ul>', '2022-06-13 13:12:55', '2022-06-13 13:12:55');
INSERT INTO `m_blogs` (`id`, `title`, `view`, `thumbnail`, `type_blog`, `slug_blog`, `content`, `created_at`, `updated_at`) VALUES
(6, 'Hướng dẫn sử dụng 2 dòng máy đo đường huyết Accu-Chek: Guide & Instant', 2, '202206132013103046-may-thu-duong-huyet-accu-chek-guide-6.jpeg', 1, 'huong-dan-su-dung-2-dong-may-do-duong-huyet-accu-chek-guide-instant', '<p><img src=\"https://cdn.bookingcare.vn/fr/w800/2022/04/22/103046-may-thu-duong-huyet-accu-chek-guide-6.jpg\" alt=\"Hướng dẫn sử dụng máy đo đường huyết Accu-Chek Guide và Instant\">Hướng dẫn sử dụng máy đo đường huyết Accu-Chek - Ảnh: BookinCare</p><p class=\"ql-align-justify\"><a href=\"https://bookingcare.vn/cam-nang/may-do-duong-huyet-accu-chek-co-tot-khong-nen-mua-loai-may-nao-p2707.html\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: var(--mau-bc-16);\">Máy đo đường huyết Accu-Chek</a>&nbsp;là thương hiệu của hãng sản xuất thiết bị y tế hàng đầu thế giới Roche có trụ sở tại Thuỵ Sĩ. Dưới đây là nội dung tóm tắt cùng video chi tiết hướng dẫn sử dụng máy đo đường huyết&nbsp;<a href=\"https://bookingcare.vn/cam-nang/so-sanh-may-do-duong-huyet-accu-chek-guide-vs-accu-chek-instant-p2705.html\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: var(--mau-bc-16);\">Accu-Chek Guide và Instant</a>&nbsp;- hai dòng máy đang được nhiều người sử dụng.</p><h2 class=\"ql-align-justify\">Hướng dẫn sử dụng máy đo đường huyết Accu-Chek Guide</h2><h3 class=\"ql-align-justify\">1. Thành phần máy đo đường huyết Accu-Chek Giude</h3><p class=\"ql-align-justify\">Thông thường, khi mua máy đo đường huyết&nbsp;<a href=\"https://bookingcare.vn/may-do-duong-huyet-accu-chek-guide-svi25?dm=5\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: var(--mau-bc-16);\">Accu-Chek Guide</a>, một bộ được đóng gói sẽ bao gồm:</p><ol><li class=\"ql-align-justify\">Máy đo đường huyết Accu-Chek Guide</li><li class=\"ql-align-justify\">Bút lấy máu Accu-Chek Fastclick</li><li class=\"ql-align-justify\">Trụ kim lấy máu</li><li class=\"ql-align-justify\">Pin</li></ol><p class=\"ql-align-justify\">Que thử không đi kèm cùng máy và bạn đọc cần mua riêng. Mỗi loại máy đo đường huyết của Accu-Chek sẽ có một loại que thử riêng, bạn đọc cần lưu ý điều này để mua đúng loại que thử.</p><h3 class=\"ql-align-justify\">2. Cấu tạo máy đo đường huyết Accu-Chek Guide</h3><p><img src=\"https://bookingcare.vn/files/blog/2022/04/20/093850-cau-tao-may-do-duong-huyet-accu-chek.jpg\" alt=\"cấu tạo máy đo đường huyết accu-chek Guide\">Cấu tạo máy đo đường huyết Accu-Chek Guide - Ảnh:&nbsp;Accu-chek</p><p><strong>1. Màn hình</strong>:&nbsp;Hiển thị kết quả, thông báo và kết quả đo được lưu trong bộ nhớ</p><p><strong>2. Nút quay lại:</strong>&nbsp;Quay lại màn hình hoặc trường trước đó</p><p><strong>3. Nút mũi tên lên và xuống:</strong>&nbsp;Ấn để di chuyển giữa các lựa chọn trong menu hoặc để tăng hay giảm số</p><p><strong>4. Nút tắt/mở/cài đặt/OK:</strong>&nbsp;Bật và tắt máy đo và cài đặt các lựa chọn</p><p><strong>5. Khe cắm que thử có đèn:</strong>&nbsp;Lắp que thử vào đây</p><p><strong>6. Nắp pin:</strong>&nbsp;Bật mở nắp để thay pin</p><p><strong>7. Cổng Micro USB:</strong>&nbsp;Truyền tải dữ liệu từ máy đo đến máy tính (PC)</p><p><strong>8. Bộ nhả que thử:</strong>&nbsp;Ấn để rút que thử ra</p><p><img src=\"https://bookingcare.vn/files/blog/2022/04/20/103011-thanh-phan-may-do-duong-huyet.jpg\" alt=\"thành phần máy đo đường huyết accu-chek\">Thành phần máy đo đườn huyết Accu-Chek Guide - Ảnh: Accu-chek</p><p><strong>9. Lọ chứa que thử</strong></p><p><strong>10. Que thử -</strong>&nbsp;Đầu điện cực kim loại: Lắp đầu này vào máy đo</p><p>&nbsp;</p><p><strong>11. Que thử -</strong>&nbsp;Cạnh màu vàng: Cho giọt máu hoặc dung dịch chứng tiếp xúc vào đây</p><p><strong>12. Cáp USB:</strong>&nbsp;Nối máy đo với máy tính</p><p><strong>13. Pin</strong></p><h3 class=\"ql-align-justify\">3. Hướng dẫn cài đặt máy đo đường huyết Accu-Chek Guide</h3><p class=\"ql-align-justify\"><strong>Cài đặt tiếng</strong></p><p class=\"ql-align-justify\"><a href=\"https://bookingcare.vn/cam-nang/may-do-duong-huyet-accu-chek-co-tot-khong-nen-mua-loai-may-nao-p2707.html\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: var(--mau-bc-16);\">Máy đo đường huyết Accu-Chek</a>&nbsp;Guide có chữ hiển thị trên màn hình là tiếng Anh/Đức/Pháp... (vì máy sản xuất tại Mỹ) nên bạn đọc có thể lựa chọn tiếng Anh để dễ sử dụng nhất. Tuy nhiên, nếu sử dụng cho người cao tuổi/người không rõ tiếng Anh sẽ cần cài đặt sẵn và hướng dẫn kĩ trước khi sử dụng.</p><p class=\"ql-align-justify\">Lưu ý, khi lựa chọn cài đặt tiếng xong, bạn đọc sẽ không thay đổi được tiếng, và cần phải liên hệ nhà sản xuất để thay đổi tiếng cho máy.</p><p class=\"ql-align-justify\"><strong>Cài đặt thời gian</strong></p><p class=\"ql-align-justify\">Bạn có thể lựa chọn cài đặt 12 giờ (12 hours) hoặc 24 giờ (24 hours):</p><ul><li class=\"ql-align-justify\">12 giờ (12 hours): lựa chọn để theo dõi giờ theo giờ sáng chiều, ví dụ 6 giờ sáng (6:00am),6 giờ chiều (6:00 pm)</li><li class=\"ql-align-justify\">24 giờ (24 hours): Lựa chọn theo dõi giờ theo 24 giờ, ví dụ: 6 giờ sáng (6:00),18 giờ (18:00)</li></ul><p class=\"ql-align-justify\">Bạn có thể nhấn nút lên xuống để cài đặt thời gian thực.&nbsp;Sau khi cài đặt tiếng và thời gian, máy đã sẵn sàng sử dụng để đo đường huyết.</p><p class=\"ql-align-justify\">Bên cạnh đó, máy đo sẽ có hai loại cho 2 đơn vị đo&nbsp;mg/dL hoặc mmol/L (không giống các máy khác thường có thể cài đặt tùy chọn 2 đơn vị đo này). Vì vậy khi mua bạn đọc nên lưu ý để lựa chọn loại máy đo có đơn vị phù hợp với vị trí địa lý của mình.</p><h3 class=\"ql-align-justify\">4. Hướng dẫn sử dụng bút lấy máu Accu-Chek FastClick</h3><p><img src=\"https://bookingcare.vn/files/blog/2022/04/20/112221-huong-dan-su-dung-but-lay-mau-fastclick.jpg\" alt=\"hướng dẫn sử dụng bút lấy máu fastclick\">Hướng dẫn sử dụng bút lấy máu FastClick - Ảnh:&nbsp;accu-chek</p><p class=\"ql-align-justify\">Chuẩn bị: Cần rửa tay bằng xà phòng và nước ấm trước khi thực hiện lắp kim và lấy máu.</p><p class=\"ql-align-justify\"><strong>Lắp hộp kim</strong></p><ol><li class=\"ql-align-justify\">Tháo nắp thiết bị lấy máu ra</li><li class=\"ql-align-justify\">Lắp hộp kim mới: Đưa đầu trắng vào thiết bị lấy máu</li></ol><p class=\"ql-align-justify\">Không cùng lúc lắp hộp kim và nhấn nút bấm phóng kim. Không lắp hộp kim khi nút bấm phóng kim đè lên một mặt phẳng, chẳng hạn như mặt bàn. Làm như vậy kim có thể bị phóng ra và vô tình đâm vào bạn.</p><p class=\"ql-align-justify\">Hộp kim được lắp đúng khi đã hoàn toàn nằm bên trong thiết bị lấy máu và chỉ có cạnh phóng kim nhô ra khỏi thiết bị lấy máu ở phía trước . Đóng nắp thiết bị lại. Phần khuyết ở gờ nắp bút phải khớp với phần cong có hình dạng tương ứng của vạch chỉ độ sâu đâm kim (dấu vuông bạc)&nbsp;</p><p class=\"ql-align-justify\">Nắp thiết bị sẽ vào đúng vị trí khi bạn nhấn vào hết mức, rồi nghe thấy tiếng tích và cảm thấy nó khớp vào vị trí. Nắp thiết bị chỉ có thể đóng được vào đến vị trí dừng khi hộp kim nằm hoàn toàn trong thiết bị lấy máu. Không cố ép để đóng nắp thiết bị vì có thể gây hư nắp và thiết bị lấy máu. Có thể điều chỉnh lại vị trí của hộp kim để đóng được nắp.</p><p class=\"ql-align-justify\">Mỗi hộp kim chứa 6 kim. Bây giờ kim thứ nhất đã sẵn sàng để dùng. Cửa sổ báo số kim khả dụng là 6; tức đang có 6 kim cho bạn dùng.</p><p class=\"ql-align-justify\"><strong>Lưu ý:&nbsp;</strong></p><ul><li class=\"ql-align-justify\">Không tháo hộp kim nếu chưa dùng hết 6 kim. Hộp kim sẽ không thể lắp lại được một khi đã được tháo ra khỏi thiết bị lấy máu.</li><li class=\"ql-align-justify\">Có thể chọn độ sâu đâm kim thấp khi mới sử dụng để tránh chấn thương, thường là mức 2.</li><li class=\"ql-align-justify\">Chỉ sử dụng 1 kim cho 1 lần lấy máu để tránh nguy cơ lây nhiễm và nhiễm trùng.</li></ul><p><br></p>', '2022-06-13 13:13:53', '2022-06-13 13:13:53');
INSERT INTO `m_specical_lists` (`id`, `namespecical`, `thumbnail`, `description_specical`, `slugspecical`, `created_at`, `updated_at`) VALUES
(1, 'Cơ Xương Khớp', '202206131944120331-co-xuong-khop.jpeg', '<h2 class=\"ql-align-justify\"><strong style=\"color: inherit;\">Bác sĩ Cơ Xương Khớp giỏi</strong></h2><p class=\"ql-align-justify\">Danh sách các bác sĩ uy tín đầu ngành Cơ Xương Khớp tại Việt Nam:</p><ul><li class=\"ql-align-justify\">Các chuyên gia có quá trình đào tạo bài bản, nhiều kinh nghiệm</li><li class=\"ql-align-justify\">Các giáo sư, phó giáo sư đang trực tiếp nghiên cứu và giảng dạy tại Đại học Y khoa Hà Nội</li><li class=\"ql-align-justify\">Các bác sĩ đã, đang công tác tại các bệnh viện hàng đầu Khoa Cơ Xương Khớp - Bệnh viện Bạch Mai, Bệnh viện Hữu nghị Việt Đức,Bệnh Viện E.</li><li class=\"ql-align-justify\">Là thành viên hoặc lãnh đạo các tổ chức chuyên môn như: Hiệp hội Cơ Xương Khớp, Hội Thấp khớp học,...</li><li class=\"ql-align-justify\">Được nhà nước công nhận các danh hiệu Thầy thuốc Nhân dân, Thầy thuốc Ưu tú, Bác sĩ Cao cấp,...</li></ul><h2 class=\"ql-align-justify\"><strong>Bệnh Cơ Xương Khớp</strong></h2><ul><li class=\"ql-align-justify\">Gout</li><li class=\"ql-align-justify\">Thoái hóa khớp: khớp gối, cột sống thắt lưng, cột sống cổ</li><li class=\"ql-align-justify\">Viêm khớp dạng thấp, Viêm đa khớp, Viêm gân</li><li class=\"ql-align-justify\">Tràn dịch khớp gối, Tràn dịch khớp háng, Tràn dịch khớp khủy, Tràn dịch khớp vai</li><li class=\"ql-align-justify\">Loãng xương, đau nhức xương</li><li class=\"ql-align-justify\">Viêm xương, gai xương</li><li class=\"ql-align-justify\">Viêm cơ, Teo cơ, chứng đau mỏi cơ</li><li class=\"ql-align-justify\">Yếu cơ, Loạn dưỡng cơ</li><li class=\"ql-align-justify\">Các chấn thương về cơ, xương, khớp</li><li class=\"ql-align-justify\">...</li></ul><p><br></p>', 'co-xuong-khop', '2022-06-13 12:44:53', '2022-06-13 12:44:53'),
(2, 'Thần kinh', '202206131945121042-than-kinh.jpeg', '<h2 class=\"ql-align-justify\"><strong>Bác sĩ Thần kinh giỏi</strong></h2><p class=\"ql-align-justify\">Danh sách các giáo sư, bác sĩ chuyên khoa Thần kinh giỏi:</p><ul><li class=\"ql-align-justify\">Các giáo sư, bác sĩ uy tín đầu ngành chuyên khoa Thần kinh đã và đang công tác tại các bệnh viện lớn như: Bệnh viện Bạch Mai, Bệnh viện Việt Đức, Bệnh viện 108, Bệnh viện Đại học Y Hà Nội, Bệnh viện 103.</li><li class=\"ql-align-justify\">Là thành viên hoặc lãnh đạo các tổ chức chuyên môn như: Hội Thần kinh Việt Nam, Hội Phẫu thuật Thần kinh...</li><li class=\"ql-align-justify\">Được nhà nước công nhận các danh hiệu Thầy thuốc nhân dân, thầy thuốc ưu tú, bác sĩ cao cấp.</li></ul><h2 class=\"ql-align-justify\"><strong>Khám bệnh chuyên khoa Thần kinh</strong></h2><ul><li class=\"ql-align-justify\">Bại Não&nbsp;&nbsp;&nbsp;</li><li class=\"ql-align-justify\">Đau đầu, chóng mặt, buồn nôn&nbsp;&nbsp;</li><li class=\"ql-align-justify\">Bệnh Pakison, bệnh tiền đình&nbsp;&nbsp;</li><li class=\"ql-align-justify\">Bị co cơ, căng dây thần kinh&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</li><li class=\"ql-align-justify\">Động kinh, có những cơn vãng ý thức&nbsp;&nbsp;</li><li class=\"ql-align-justify\">Bị tê bì nửa mặt, chèn dây thần kinh</li><li class=\"ql-align-justify\">Bồn chồn, lo lắng, hồi hộp, chân tay run&nbsp;&nbsp;</li><li class=\"ql-align-justify\">Có dấu hiệu tăng động&nbsp;&nbsp;&nbsp;</li><li class=\"ql-align-justify\">Co rút cổ, đau đầu với mặt,&nbsp;chân tay, vã mồ hôi&nbsp;&nbsp;</li><li class=\"ql-align-justify\">Chấn thương đầu, dây thần kinh</li><li class=\"ql-align-justify\">...</li><li class=\"ql-align-justify\"><br></li></ul>', 'than-kinh', '2022-06-13 12:45:24', '2022-06-13 12:45:24'),
(3, 'Tiêu hóa', '202206131945120933-tieu-hoa.jpeg', '<h2 class=\"ql-align-justify\"><strong>Bác sĩ Chuyên khoa Tiêu hóa</strong></h2><p class=\"ql-align-justify\">Danh sách các bác sĩ Tiêu hóa uy tín đầu ngành tại Việt Nam:</p><ul><li class=\"ql-align-justify\">Các chuyên gia có quá trình đào tạo bài bản, kinh nghiệm công tác tại các bệnh viện lớn về chuyên ngành Tiêu hóa tại Hà Nội</li><li class=\"ql-align-justify\">Các giáo sư, phó giáo sư là giảng viên Đại học Y khoa Hà Nội</li><li class=\"ql-align-justify\">Các bác sĩ đã, đang công tác tại các bệnh viện hàng đầu như Bệnh viện Bạch Mai, Bệnh Viện Việt Đức, Bệnh Viện Nhi Trung ương, Bệnh viện Y học Cổ truyền Việt Nam...</li><li class=\"ql-align-justify\">Được nhà nước công nhận các danh hiệu Thầy thuốc nhân dân, thầy thuốc ưu tú, bác sĩ cao cấp,...</li></ul><p class=\"ql-align-justify\"><strong>Tư vấn, khám và điều trị các Bệnh Tiêu hóa</strong></p><ul><li class=\"ql-align-justify\">Ăn uống kém, không ngon</li><li class=\"ql-align-justify\">Rối loạn tiêu hóa, táo bón, trĩ</li><li class=\"ql-align-justify\">Nhiễm vi khuẩn HP (Helicobacter pylori)</li><li class=\"ql-align-justify\">Nội soi dạ dày, đại tràng, tiêu hóa</li><li class=\"ql-align-justify\">Buồn nôn, chướng bụng, đầy bụng ợ chua, đầy hơi</li><li class=\"ql-align-justify\">Co thắt thực quản,&nbsp;Hội chứng ruột kích thích</li><li class=\"ql-align-justify\">Đau bụng,&nbsp;dạ dày, đại tràng, thượng vị</li><li class=\"ql-align-justify\">Viêm đại tràng, dạ dày,&nbsp;tá tràng</li><li class=\"ql-align-justify\">Ung thư dạ dày, U nang tuyến tụy</li><li class=\"ql-align-justify\">Bệnh lý về gan, mật</li><li class=\"ql-align-justify\">...</li><li class=\"ql-align-justify\"><br></li></ul>', 'tieu-hoa', '2022-06-13 12:45:52', '2022-06-13 12:45:52'),
(4, 'Tim mạch', '202206131946120741-tim-mach.jpeg', '<h2 class=\"ql-align-justify\"><strong>Bác sĩ tim mạch giỏi</strong></h2><p class=\"ql-align-justify\">Danh dách các bác sĩ tim mạch uy tín đầu ngành tại Việt Nam:</p><ul><li class=\"ql-align-justify\">Các chuyên gia được đào tạo bài bản về chuyên ngành Tim mạch tại các trường đại học trong nước và quốc tế.</li><li class=\"ql-align-justify\">Các giáo sư, phó giáo sư nghiên cứu và giảng dạy tại Đại học Y Hà Nội</li><li class=\"ql-align-justify\">Các bác sĩ đã, đang công tác tại các bệnh viện hàng đầu như Viện Tim Mạch Quốc Gia, Bệnh viện Bạch Mai, Bệnh viện Việt Đức, Bệnh Viện E, Bệnh Viện Tim Hà Nội</li><li class=\"ql-align-justify\">Là thành viên hoặc lãnh đạo các tổ chức chuyên môn như: Hiệp hội Tim Mạch Việt Nam</li><li class=\"ql-align-justify\">Đạt danh hiệu Thầy thuốc Nhân dân, Thầy thuốc Ưu tú, Bác sĩ Cao cấp,...</li></ul><p class=\"ql-align-justify\"><strong>Khám tư vấn tim mạch</strong></p><ul><li class=\"ql-align-justify\">Khó thở, Đau ngực, đau tim</li><li class=\"ql-align-justify\">Tăng huyết áp, hạ huyết áp</li><li class=\"ql-align-justify\">Rối loạn mỡ máu, cao huyết áp, chóng mặt</li><li class=\"ql-align-justify\">Bệnh van tim (Hẹp hở van tim),&nbsp;Hẹp động mạch chủ</li><li class=\"ql-align-justify\">Cảm giác hồi hộp, tim đập nhanh&nbsp;&nbsp;</li><li class=\"ql-align-justify\">Tim bẩm sinh, có tiền sử bệnh tim to,&nbsp;tiền sử tai biến&nbsp;&nbsp;</li><li class=\"ql-align-justify\">Đã đặt stent tim,&nbsp;nong động mạch vành</li><li class=\"ql-align-justify\">Giãn tĩnh mạch chân&nbsp;&nbsp;</li><li class=\"ql-align-justify\">...</li><li class=\"ql-align-justify\"><br></li></ul>', 'tim-mach', '2022-06-13 12:46:27', '2022-06-13 12:46:27'),
(5, 'Tai Mũi Họng', '202206131946121146-tai-mui-hong.jpeg', '<h2 class=\"ql-align-justify\"><strong>Bác sĩ Chuyên khoa Tai Mũi Họng</strong></h2><p class=\"ql-align-justify\"><strong>Danh sách các bác sĩ uy tín đầu ngành tại Việt Nam:</strong></p><ul><li class=\"ql-align-justify\">Các chuyên gia có quá trình đào tạo bài bản, kinh nghiệm công tác tại các bệnh viện lớn về chuyên ngành Tai Mũi Họng tại Hà Nội</li><li class=\"ql-align-justify\">Các giáo sư, phó giáo sư là giảng viên Đại học Y khoa Hà Nội</li><li class=\"ql-align-justify\">Các bác sĩ đã, đang công tác tại các bệnh viện hàng đầu như Bệnh viện Bạch Mai, Bệnh Viện Tai Mũi Họng Trung ương, Bệnh viện Quân Y 108...</li><li class=\"ql-align-justify\">Được nhà nước công nhận các danh hiệu Thầy thuốc Nhân dân, Thầy thuốc Ưu tú, Bác sĩ cao cấp,...</li></ul><p class=\"ql-align-justify\"><strong style=\"color: inherit;\">Các bệnh Tai Mũi Họng</strong></p><ul><li class=\"ql-align-justify\">Ù tai, đau tai, chảy máu tai</li><li class=\"ql-align-justify\">Thủng màng nhĩ, điếc đột ngột</li><li class=\"ql-align-justify\"><br></li><li>Viêm tai giữa</li><li>Amidan, V.A</li><li class=\"ql-align-justify\">Viêm xoang</li><li class=\"ql-align-justify\">Nghẹt mũi</li><li class=\"ql-align-justify\">Hay bị chảy máu cam</li><li>Đau cổ họng, khó nuốt</li><li class=\"ql-align-justify\">Ho kéo dài</li><li class=\"ql-align-justify\">Ngủ ngáy</li><li class=\"ql-align-justify\">...</li></ul>', 'tai-mui-hong', '2022-06-13 12:46:52', '2022-06-13 12:46:52');
